CREATE TABLE `workflow`.`google_accounts` ( `id` INT NOT NULL AUTO_INCREMENT , `proxyId` INT NOT NULL , `cookieId` INT NOT NULL , `avatarId` INT NOT NULL , `email` VARCHAR(125) NOT NULL , `password` VARCHAR(255) NOT NULL , `rEmail` VARCHAR(255) NULL DEFAULT NULL , `phone` VARCHAR(50) NULL DEFAULT NULL , `loginAt` DATETIME NOT NULL , `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `deactivatedAt` DATETIME NULL DEFAULT NULL , `used` INT NOT NULL DEFAULT '0' , `working` BOOLEAN NOT NULL DEFAULT FALSE , `status` BOOLEAN NOT NULL DEFAULT TRUE , PRIMARY KEY (`id`)) ENGINE = InnoDB; 