CREATE TABLE `workflow`.`proxies` ( `id` INT NOT NULL AUTO_INCREMENT , `ip` VARCHAR(35) NOT NULL , `port` INT(6) NOT NULL DEFAULT '8000' , `login` VARCHAR(125) NULL DEFAULT NULL , `password` VARCHAR(125) NULL DEFAULT NULL , `language` VARCHAR(2) NOT NULL DEFAULT 'ru' , `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `deactivatedAt` DATETIME NULL DEFAULT NULL , `used` INT NOT NULL DEFAULT '0' , `status` INT NOT NULL DEFAULT '1' , PRIMARY KEY (`id`)) ENGINE = InnoDB; 